theme.css:
.igc-textual-figure {
	font-size: 45px;
}
.igc-sheets .igc-sheet .igc-sheet-ico,
.igc-sheets .igc-sheet:hover .igc-sheet-ico {
	border-color: #7d7d7d;
}
.igc-sheets .igc-sheet .igc-sheet-label,
.igc-sheets .igc-sheet:hover .igc-sheet-label,
.igc-sheets .igc-sheet.active .igc-sheet-label {
	color: #7d7d7d;
}
.igc-sheet .igc-sheet-ico,
.igc-sheet.active .igc-sheet-ico {
	background: transparent;
}
.igc-sheets .igc-sheet.active .igc-sheet-ico::after,
.igc-sheets .igc-sheet:hover .igc-sheet-ico::after {
	height: 14px;
	width: 14px;
	left: 2px;
	top: 2px;
	background: #7d7d7d;
}
div[id^='table_'] {
	margin-left: 20px;
	margin-right: 20px;
}
div[id^='table_'] &gt; .inner {
	padding-left: 0 !important;
	padding-right: 0 !important;
}
.igc-table-scroll {
	background: #dad8c1;
}
.igc-table .igc-table-cell {
	border-width: 0px;
}
.igc-table .igc-table-header {
	border-width: 0px;
}
.chart &gt; .inner {
	padding-left: 21px;
	padding-right: 21px;
}
.ig-container {
	background: #edede2;
}
#madewith a,
#madewith a:link,
#madewith a:hover,
#madewith a:visited,
#madewith a:active,
.ig-copyright {
	color: #614829 !important;
}
.ig-copyright {
	font-style: italic;
}
.headline {
	font-family: League Gothic, Roboto Condensed;
	font-size: 72px;
	color: #000;
	text-transform: uppercase;
}
.chart-title {
	font-family: League Gothic, Roboto Condensed;
	font-weight: 400;
	font-size: 41px;
	color: #000;
	border-top: 5px solid #000;
	border-bottom: 2px solid #000;
}
.bodytext {
	font-family: Noto Serif;
	font-size: 16px;
	color: #494949;
}
.quote {
	font-family: Noto Serif;
	font-style: italic;
	font-size: 20px;
	text-align: center;
	color: #edede2;
	font-weight: 400;
	position: relative;
}
.quote .quotetext {
	background: #000;
	padding-top: 20px;
	padding-left: 20px;
	margin-left: 60px;
	padding-right: 30px;
}
.quote .quotetitle {
	margin-left: 60px;
	background: #000;
	padding-bottom: 20px;
	padding-left: 20px;
	padding-right: 30px;
}
.quote::before {
	content: '';
	position: absolute;
	top: 10px;
	left: 50px;
	width: 0;
	height: 0;
	border-top: 10px solid transparent;
	border-bottom: 10px solid transparent;
	border-right: 10px solid black;
}
.quote::after {
	position: absolute;
	content: '';
	top: 5px;
	left: 0px;
	border: none;
	background: url(/i/templates/44/quote-shape.png) 0% 0% no-repeat;
	width: 43px;
	height: 31px;
}
.quotetitle {
	font-size: 14px;
	margin-top: 0;
	padding-top: 15px;
}
.map {
	background: transparent;
}
#madewith.active a:hover,
.headline.active:hover {
	color: #00b6b6 !important;
}
.titlepopup1,
.titlepopup2 {
	border-color: #00b6b6;
}
.shrink .chart &gt; .inner {
	padding-left: 10px;
	padding-right: 10px;
}
.shrink .chart {
	margin-left: 0;
	margin-right: 0;
}
.shrink .quote {
	margin-right: 0;
}
.ig-items ul:empty {
	display: none;
}
.ig-separator-line {
	background: rgba(128, 128, 128, 0.5);
}
.heatmap-legend {
	background: rgba(237, 237, 226, 0.7);
}
.heatmap-legend-item,
.heatmap-label {
	color: #000;
	font-size: 11px;
	font-family: PT Sans Narrow;
}
.igc-graph-pie-piece {
	stroke: rgba(237, 237, 226, 0.7);
}
.igc-tabs .igc-tab-active {
             background: #edede2;
          }
          .igc-tabs .igc-tab .igc-tab-content,
          .igc-tabs .igc-tab.icon-down:after {
              font-family: PT Sans Narrow;
              color: #000000;
              font-weight: 400;
          }
          .igc-tabs .igc-tab,
          .igc-tab-switcher {
              color: #000000;
          } .igc-tabs.igc-tabs-dropdown .igc-tab-name {
              font-family: PT Sans Narrow;
              color: #000000;
              font-weight: 400;
          } .captiontext {font-family: PT Sans Narrow;color: #000000;font-weight: 400;} .captiontext .innertext {line-height: 1.5;}
